mapper.select:
select v.id,v.name,v.remark,v.px,v.videourl,v.imgurl,v.quality,v.time, v.watch,s.id,s.name as sname,v.level,l.name as levelText, vi.imgurl as img,s.id as sid,s.name AS sname, c.id as cid, c.name as cname,c.type,t.name as typeText from video v left join video_img vi on vi.vid = v.id left join star s on s.id = v.sid left join video_cid m on m.vid =v.id left join category c on c.id = m.cid left join code t on t.code_id =c.type and t.type='page' left join code l on l.code_id =v.level and l.type='level'
v.name like CONCAT('%', #{name}, '%')
and c.id =#{cid}
and v.sid =#{sid}
and v.level =#{level}
and c.type =#{type}
and v.level = (select role_id from user where id = #{user_id})
order by CONVERT (c.px, SIGNED),CONVERT(v.px,SIGNED)

select count(DISTINCT v.id) from video v left join video_img vi on vi.vid = v.id left join star s on s.id = v.sid left join video_cid m on m.vid =v.id left join category c on c.id = m.cid left join code t on t.code_id =c.type and t.type='page' left join code l on l.code_id =v.level and l.type='level'
v.name like CONCAT('%', #{name}, '%')
and c.id =#{cid}
and v.sid =#{sid}
and v.level =#{level}
and c.type =#{type}
and v.level = (select role_id from user where id = #{user_id})
order by CONVERT (c.px, SIGNED),CONVERT(v.px,SIGNED)
